import React, { useState } from 'react'
import{ArrowBigLeft, ArrowBigRight} from "lucide-react"
import "./imageSlider.css"

export default function Slider({imgProp}) {
  const [imgIndex, setImgIndex] = useState(0)

  const showPrevImg = () =>{
  setImgIndex(index =>{
    if(index === 0) return imgProp.length -1;
    return index -1;
  })
}
  const showNextImg = () =>{
    setImgIndex(index =>{
      if(index === imgProp.length-1) return 0;
      return index + 1;
    })
  }
  return (
    <div style={{width: "100%", height:"100%", position:"relative"}}>
      <img src={imgProp[imgIndex]} className='img-slider'/>

      <button className='img-slider-btn' style={{left : 0}} onClick={showPrevImg}><ArrowBigLeft/></button>
      <button className='img-slider-btn' style={{right : 0}}on><ArrowBigRight/></button>
    </div>
  )
}
