import React, { useState } from 'react'
import{ArrowBigLeft, ArrowBigRight} from "lucide-react"
import "./imageSlider.css"

export default function Slider({imgProp}) {
  const [imgIndex, setImgIndex] = useState(0)

  const showPrevImg = () =>{
  setImgIndex(index =>{
    if(index === 0) return imgProp.length -1;
    return index -1;
  })
}
  const showNextImg = () =>{
    setImgIndex(index =>{
      if(index === imgProp.length-1) return 0;
      return index + 1;
    })
  }
  return (
    <div style={{width: "100%", height:"100%", position:"relative"}}>
      <div style={{width: "100%", height:"100%", display: "flex", overflow:"hidden"}}>
        {imgProp.map( url =>(
          <img key={url} src={url} className='img-slider' 
          style={{translate: `${-100 * imgIndex}%`}}
          />
        ))}
      </div>

      <button className='img-slider-btn' style={{left : 0}} onClick={showPrevImg}><ArrowBigLeft/></button>
      <button className='img-slider-btn' style={{right : 0}}onClick={showNextImg}><ArrowBigRight/></button>
      <div style={{ 
        position:"absolute",
         bottom: ".5rem",
         left : "50%",
         translate: "-50%",
         display:"flex",
         gap:".25rem"

         }}>
        {imgProp.map((_,index)=>(
          <button onClick={()=> setImgIndex(index)}>{index}</button>
        ))}
      </div>
    </div>
  )
}
