import React, { useState, useEffect } from 'react'
import{ArrowBigLeft, ArrowBigRight, Circle, CircleDot} from "lucide-react"
import "./imageSlider.css"

export default function Slider({imgProp}) {
  const [imgIndex, setImgIndex] = useState(0)

  const showPrevImg = () =>{
  setImgIndex(index =>{
    if(index === 0) return imgProp.length -1;
    return index -1;
  })
}
  const showNextImg = () =>{
    setImgIndex(index =>{
      if(index === imgProp.length-1) return 0;
      return index + 1;
    })

  }

  useEffect(() => {
    let timer = setTimeout(() => {
      setImgIndex((imgIndex) => (imgIndex + 1) % imgProp.length);   
    }, 3000);

    return () => {
      clearTimeout(timer);
    };
}, [imgIndex , imgProp]);

  return (
    <div style={{width: "100%", height:"100%", position:"relative"}}>
      <div style={{width: "100%", height:"100%", display: "flex", overflow:"hidden"}}>
        {imgProp.map( url =>(
          <img key={url} src={url} className='img-slider' 
          style={{translate: `${-100 * imgIndex}%`}}
          />
        ))}
      </div>


      <div style={{ 
        position:"absolute",
         bottom: ".5rem",
         left : "50%",
         translate: "-50%",
         display:"flex",
         gap:".25rem"

         }}>
        {imgProp.map((_,index)=>(
          <button  className="img-slider-dot-btn" onClick={()=> setImgIndex(index)}>{index === imgIndex ? <CircleDot style={{scale: "1.2"}} /> : <Circle />}</button>
        ))}
      </div>

    </div>
  )
}
