import React, { useState } from 'react'
import{ArrowBigLeft, ArrowBigRight} from "lucide-react"

import "./imageSlider.css"

const images = [
  "https://images.unsplash.com/photo-1593466144596-8abd50ad2c52?q=80&w=1934&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
  "https://images.unsplash.com/photo-1521727696769-849d83dd59db?q=80&w=1935&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
  "https://plus.unsplash.com/premium_photo-1664304102989-6048cbdc2051?q=80&w=1780&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
  "https://images.unsplash.com/photo-1588627696344-841d62bf1f7a?q=80&w=1936&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
]

export default function Slider() {
  const [imgIndex, setImgIndex] = useState(0)


  const showPrevImg = () =>{
  setImgIndex(index =>{
    if(index === 0) return images.length -1;
    return index -1;
  })
}
  const showNextImg = () =>{
    setImgIndex(index =>{
      if(index === images.length-1) return 0;
      return index + 1;
    })
  }
  return (
<> <div style={{width: "100%", height:"100%", position:"relative"}}>
<div>
    {images.map( (s) =>{
      <img src={s} />
    })}
  </div>


      <button className='img-slider-btn' style={{left : 0}} onClick={showPrevImg}><ArrowBigLeft/></button>
      <button className='img-slider-btn' style={{right : 0}} onClick={showNextImg}><ArrowBigRight/></button>
    </div>

  </>   
  )
}
