import React, { useState } from 'react'
import{ArrowBigLeft, ArrowBigRight} from "lucide-react"
import img1 from "../src/images/devon-janse-van-rensburg-yoqHLUayUTg-unsplash.jpg"
import img2 from "../src/images/erik-mclean-ZRns2R5azu0-unsplash.jpg"
import img3 from "../src/images/lance-asper-N9Pf2J656aQ-unsplash.jpg"
import img4 from "../src/images/sven-d-a4S6KUuLeoM-unsplash.jpg"
import img5 from "../src/images/zan-1BWBiUUT-AA-unsplash.jpg"
import "./imageSlider.css"

const images = [img1,img2,img3,img4,img5]

export default function Slider() {
  const [imgIndex, setImgIndex] = useState(0)


  const showPrevImg = () =>{
  setImgIndex(index =>{
    if(index === 0) return imgProp.length -1;
    return index -1;
  })
}
  const showNextImg = () =>{
    setImgIndex(index =>{
      if(index === imgProp.length-1) return 0;
      return index + 1;
    })
  }
  return (
    <div style={{width: "100%", height:"100%", position:"relative"}}>
      <div>
        {imgProp.map(url =>{
        <img  src={imgProp[img]} className='img-slider'/>
        })}
      </div>


      <button className='img-slider-btn' style={{left : 0}} onClick={showPrevImg}><ArrowBigLeft/></button>
      <button className='img-slider-btn' style={{right : 0}} onClick={showNextImg}><ArrowBigRight/></button>
    </div>
  )
}
